import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Items from './Items';
import Stocks from './Stocks';
import axios from 'axios';


function App() {
    const [selectedItem, setSelectedItem] = useState(null);
    const [stock, setStock] = useState(null);

    const handleSelectItem = (item) => {
        setSelectedItem(item);
        // Fetch stocks for the selected item
        axios.get(`http://localhost:7190/stocks`)
            .then(response => {
                if (response.status === 200) {
                    const stocks = response.data.results.filter(s => s.itemId === item.itemId);
                    setStock(stocks.length > 0 ? stocks[0] : null);
                }
            })
            .catch(error => console.error('Error fetching stocks:', error));
    };

    return (
        <div className="container">
            <Items onSelectItem={handleSelectItem} />
            <Stocks stock={stock} />
        </div>
    );
}

export default App;
