import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [items, setItems] = useState([]);
    const [selectedItemId, setSelectedItemId] = useState(null);
    const [stock, setStock] = useState(null);

    useEffect(() => {
        axios.get('https://localhost:7190/items')
            .then(response => {
                if (response.status === 200) {
                    setItems(response.data.results);
                }
            })
            .catch(error => console.error('Error fetching items:', error));
    }, []);

    const handleSelectItem = (itemId) => {
        setSelectedItemId(itemId);
        axios.get(`https://localhost:7190/stocks?itemId=${itemId}`)
            .then(response => {
                if (response.status === 200) {
                    setStock(response.data.results[0] || null); // Assuming each item has a single stock entry
                }
            })
            .catch(error => console.error('Error fetching stocks:', error));
    };

    return (
        <div className="container mt-4">
            <h2>Inventory Items</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Item ID</th>
                        <th>Name</th>
                        <th>Code</th>
                        <th>Measuring Unit</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {items.map(item => (
                        <tr key={item.itemId}>
                            <td>{item.itemId}</td>
                            <td>{item.name}</td>
                            <td>{item.code}</td>
                            <td>{item.measuringUnitName}</td>
                            <td>
                                <button className="btn btn-info" onClick={() => handleSelectItem(item.itemId)}>
                                    Check Stocks
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {selectedItemId && stock && (
                <div>
                    <h4>Stock Details for Item ID: {selectedItemId}</h4>
                    <table className="table table-bordered">
                        <tbody>
                            <tr>
                                <th>Warehouse</th>
                                <td>{stock.name}</td>
                            </tr>
                            <tr>
                                <th>Address</th>
                                <td>{stock.address}</td>
                            </tr>
                            <tr>
                                <th>Manager</th>
                                <td>{stock.manger.name}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default App;
