import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [items, setItems] = useState([]);
    const [selectedItemId, setSelectedItemId] = useState(null);
    const [stock, setStock] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [newItem, setNewItem] = useState({
        name: '',
        code: '',
        measuringUnitId: ''
    });

    const measuringUnits = [
        { id: 4, name: 'Kilogram' },
        { id: 5, name: 'Litres' },
        { id: 6, name: 'Piece' },
        { id: 7, name: 'Dozen' },
        { id: 8, name: 'Packet' },
        { id: 9, name: 'Pairs' }
    ];

    useEffect(() => {
        fetchItems();
    }, []);

    const fetchItems = () => {
        axios.get('https://localhost:7190/items')
            .then(response => {
                if (response.status === 200) {
                    setItems(response.data.results);
                }
            })
            .catch(error => console.error('Error fetching items:', error));
    };

    const handleSelectItem = (itemId) => {
        setSelectedItemId(itemId);
        axios.get(`https://localhost:7190/stocks?itemId=${itemId}`)
            .then(response => {
                if (response.status === 200) {
                    setStock(response.data.results[0] || null);
                }
            })
            .catch(error => console.error('Error fetching stocks:', error));
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setNewItem({ 
            ...newItem, 
            [name]: name === 'measuringUnitId' ? parseInt(value) : value 
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Submitting:', newItem);
        axios.post('https://localhost:7190/items', newItem)
            .then(response => {
                console.log("Response from POST request:", response);
                if (response.status === 200 || response.status === 201) {
                    fetchItems();
                    setShowModal(false);
                    setNewItem({ name: '', code: '', measuringUnitId: '' });  // Reset form
                }
            })
            .catch(error => console.error('Error posting item:', error));
    };

    return (
        <div className="container mt-4">
            <div className="d-flex justify-content-between align-items-center">
                <h2>Inventory Items</h2>
                <button className="btn btn-primary" onClick={() => setShowModal(true)}>Add Item</button>
            </div>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Item ID</th>
                        <th>Name</th>
                        <th>Code</th>
                        <th>Measuring Unit</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {items.map(item => (
                        <tr key={item.itemId}>
                            <td>{item.itemId}</td>
                            <td>{item.name}</td>
                            <td>{item.code}</td>
                            <td>{item.measuringUnitName}</td>
                            <td>
                                <button className="btn btn-info" onClick={() => handleSelectItem(item.itemId)}>
                                    Check Stocks
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {selectedItemId && stock && (
                <div>
                    <h4>Stock Details for Item ID: {selectedItemId}</h4>
                    <table className="table table-bordered">
                        <tbody>
                            <tr>
                                <th>Warehouse</th>
                                <td>{stock.name}</td>
                            </tr>
                            <tr>
                                <th>Address</th>
                                <td>{stock.address}</td>
                            </tr>
                            <tr>
                                <th>Manager</th>
                                <td>{stock.manger.name}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            )}
            {showModal && (
                <div className="modal show d-block" tabIndex="-1">
                    <div className="modal-dialog">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Add New Item</h5>
                                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
                            </div>
                            <div className="modal-body">
                                <form onSubmit={handleSubmit}>
                                    <div className="mb-3">
                                        <label className="form-label">Name</label>
                                        <input type="text" className="form-control" name="name" value={newItem.name} onChange={handleInputChange} required />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Code</label>
                                        <input type="text" className="form-control" name="code" value={newItem.code} onChange={handleInputChange} required />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Measuring Unit</label>
                                        <select className="form-select" name="measuringUnitId" value={newItem.measuringUnitId} onChange={handleInputChange} required>
                                            <option value="">Select Unit</option>
                                            {measuringUnits.map(unit => (
                                                <option key={unit.id} value={unit.id}>{unit.name}</option>
                                            ))}
                                        </select>
                                    </div>
                                    <button type="submit" className="btn btn-primary">Submit</button>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
